document.addEventListener("DOMContentLoaded",()=>{const e=document.querySelectorAll(".scroll-item"),t=()=>{let t=window.innerHeight+window.scrollY;e.forEach(e=>{let o=e.offsetTop+e.offsetHeight/2;t>=o?e.classList.add("animation-class"):e.classList.remove("animation-class")})};t(),window.addEventListener("scroll",()=>{t()})});