const menuLinks=document.querySelectorAll(".menu__link[data-goto]");if(console.log(menuLinks),menuLinks.length>0){function onMenuLinkClick(e){e.preventDefault();const n=e.target,t=document.querySelector(n.dataset.goto).getBoundingClientRect().top+pageYOffset;n.classList.add("menu__link--active"),window.scrollTo({top:t,behavior:"smooth"})}menuLinks.forEach(e=>{e.addEventListener("click",onMenuLinkClick)})}